"""Day 1: 'Not Quite Lisp' https://adventofcode.com/2015/day/1"""

import pytest


def day_1_2017_part_1(seq):
    position = 0
    for char in seq:
        if char == '(':
            position += 1
        else:
            position += -1
    return position


@pytest.mark.parametrize("inp,expected", [
    ('(())', 0),
    ('()()', 0),
    ('(((', 3),
    ('(()(()(', 3),
    ('))(((((', 3),
    ('())', -1),
    ('))(', -1),
    (')))', -3),
    (')())())', -3),
])
def test_day_2part_1_cases(inp, expected):
    computed = day_1_2017_part_1(inp)
    assert computed == expected

# Part 2 ----------------------

def day_1_2017_part_2(seq):
    position = 0
    for i, char in enumerate(seq):
        if char == '(':
            position += 1
        else:
            position += -1
        if position == -1:
            return i + 1


@pytest.mark.parametrize("inp,expected", [
    (')', 1),
    ('()())', 5),
])
def test_day_2part_2_cases(inp, expected):
    computed = day_1_2017_part_2(inp)
    assert computed == expected


